CREATE TYPE double_edged_type AS ENUM ('inner_state', 'associate_with_people', 'tackle_issues', 'face_choices', 'common_outcome', 'normal_state');

CREATE TABLE double_edgeds (
    id SERIAL PRIMARY KEY,
    dimension VARCHAR(255) NOT NULL,
    like_element_id INTEGER NOT NULL,
    talent_element_id INTEGER NOT NULL,
    content TEXT NOT NULL,
    type double_edged_type NOT NULL,
    FOREIGN KEY (like_element_id) REFERENCES elements(id),
    FOREIGN KEY (talent_element_id) REFERENCES elements(id)
);


